    public static String getStationName(JSONObject jsonObject) throws JSONException {
        JSONObject stationInfo = jsonObject.getJSONObject("station_info");

        return stationInfo.getString("name");
    }

    public static ArrayList<String> getHours(JSONObject jsonObject) throws JSONException {
        JSONArray weather = jsonObject.getJSONArray("weather");

        ArrayList<String> hodiny = new ArrayList<>();

        for (int i = 0; i < 24; i++) {
            String hodina = weather.getJSONObject(i).getString("hodina");
            hodiny.add(hodina);
        }
        return hodiny;
    }

    public static ArrayList<Entry> getTemperature(JSONObject jsonObject) throws JSONException {
        JSONArray weather = jsonObject.getJSONArray("weather");

        ArrayList<Entry> teploty = new ArrayList<>();

        for (int i = 0; i < 24; i++) {
            int teplota = weather.getJSONObject(i).getInt("teplota");
            teploty.add(new Entry(teplota, i));
        }
        return teploty;
    }

    public static ArrayList<BarEntry> getRain(JSONObject jsonObject) throws JSONException {
        JSONArray weather = jsonObject.getJSONArray("weather");

        ArrayList<BarEntry> zrazky = new ArrayList<>();

        for (int i = 0; i < 24; i++) {
            int zrazka = weather.getJSONObject(i).getInt("zrazky");
            zrazky.add(new BarEntry(zrazka, i));
        }
        return zrazky;
    }
